import { useState } from "react"
import axios from "axios"

function Contact() {
  const [formData, setFormData] = useState({ name: "", email: "", message: "" })
  const [status, setStatus] = useState("")

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setStatus("Sending...")

    try {
      const res = await axios.post(
        "http://localhost:5000/api/contact",
        formData
      )
      setStatus("Message sent!")
      setFormData({ name: "", email: "", message: "" })
    } catch (err) {
      setStatus("Error sending message.")
    }
  }

  return (
    <section className="py-12 px-6 bg-white max-w-6xl mx-auto animate-fadeIn">
      <h3 className="text-2xl font-semibold text-center mb-8">Contact Us</h3>
      <form
        className="max-w-xl mx-auto flex flex-col gap-4"
        onSubmit={handleSubmit}
      >
        <input
          name="name"
          type="text"
          placeholder="Your Name"
          className="p-3 border rounded focus:ring-2 focus:ring-blue-600"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          name="email"
          type="email"
          placeholder="Your Email"
          className="p-3 border rounded focus:ring-2 focus:ring-blue-600"
          value={formData.email}
          onChange={handleChange}
        />
        <textarea
          name="message"
          placeholder="Your Message"
          rows="4"
          className="p-3 border rounded focus:ring-2 focus:ring-blue-600"
          value={formData.message}
          onChange={handleChange}
        ></textarea>
        <button
          type="submit"
          className="bg-blue-700 text-white px-6 py-3 rounded hover:bg-blue-800 transition"
        >
          Send Message
        </button>
        <p className="text-center text-sm text-gray-600">{status}</p>
      </form>
    </section>
  )
}

export default Contact
